(()=>{"use strict";new Vue({el:"#basket",data:{items:[],totalPrice:null,totalAmount:null,showProduct:!1,url:"https://raw.githubusercontent.com/kellolo/static/master/JSON/basket.json"},computed:{priceSum(){let t=0;for(let e of this.items)t+=+e.productPrice*+e.amount;return t}},methods:{get:t=>fetch(t).then((t=>t.json())),deleteBasket(t,e){this.items[e].amount?(t.amount-=1,console.log(t.amount)):this.items.splice(e,1)},addBasket(t){console.log(t.amount),t.amount+=1}},mounted(){this.get(this.url).then((t=>{this.items=t.content,this.totalPrice=t.totalPrice,this.totalAmount=t.amount,console.log(t)}))},update(){}})})();